const React = require('react');
const classnames = require('classnames');
const Context = require('@ali/tingle-context');

const prefixClass = function(name) {
    return Context.prefixClass ? Context.prefixClass(name) : 't-' + name
}

class SlideNav extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    render() {
        let t = this;
        let arr = [];
        for (let i = 0; i < t.props.num; i++) {
            arr.push(<div key={i} className={classnames({
                [`${prefixClass('M2 slide-nav-item')}`]: true,
                "active": i == t.props.active
            })}></div>)
        }
        return (
            <div className={`${prefixClass('slide-nav FBH FBAC FBJC')}`}>
                {arr}
            </div>
        )
    }
}

SlideNav.defaultProps = {
    num: 0,
    active: 0
};

// http://facebook.github.io/react/docs/reusable-components.html
SlideNav.propTypes = {
    num: React.PropTypes.number,
    active: React.PropTypes.number
};

SlideNav.displayName='SlideNav';

module.exports = SlideNav;
